import { useSelector } from "react-redux";
const LOAD_USERS_TO_FOLLOW = "follows/LOAD_USERS_TO_FOLLOW";

const loadUsersToFollow = () => ({
  type: LOAD_USERS_TO_FOLLOW,
  payload: usersToFollow,
});

export const getUsersToFollow = (userId) => async (dispatch) => {
  const usersFetch = await fetch("/api/users/");
  const usersFetchData = await usersFetch.json();
  const users = [usersFetchData.users];

  const following = useSelector((state) => state.follows.followings);
  const followingArr = Object.values(following || {});
  const usersToFollow = [];
  const followingSet = new Set();
  for (let i = 0; i < followingArr.length; i++) {
    const user = followingArr[i];
    followingSet.add(user.id);
  }
  for (let i = 0; i < users.length; i++) {
    const user = users[i];
    if (!followingSet.has(user.id)) {
      usersToFollow.push(user);
    }
  }
  return loadUsersToFollow(usersToFollow);
};

export default function usersTFollowReducer(state = {}, action) {
  switch (action.type) {
    case LOAD_USERS_TO_FOLLOW:
      const loaderState = { ...state, usersToFollow: {} };
      action.payload.usersToFollow?.forEach(
        (user) => (loaderState.usersToFollow[user.id] = user)
      );
      return loaderState;
    default:
      return state;
  }
}
